import React, { useState } from "react"
import { Card, Button, Form } from "react-bootstrap"
import { useAuth } from "../contexts/AuthContext"
import { Link, useHistory } from "react-router-dom"

export default function Add() {
  const [error, setError] = useState("")
  const { currentUser, logout } = useAuth()
  const history = useHistory()
  const [checked, setCheck] = useState(false)

  async function handleLogout() {
    setError("")

    try {
      //await logout()
      // need an await for form control
      history.push("/")
    } catch {
      setError("Failed to log out")
    }
  }

  const handleChange = (event) => {
     setCheck(event.target.checked);


  }

  return (
    <>
      <Card>
        <Card.Body>
        <Form>
  <Form.Group className="mb-3" controlId="formBasicEmail">
    <Form.Label>Medicine Name</Form.Label>
    <Form.Control type="text" placeholder="Name" />
  </Form.Group>

  <Form.Group className="mb-3" controlId="formBasicPassword">
    <Form.Label>Medicine Interval</Form.Label>
    <Form.Control type="number" placeholder="Interval" />
  </Form.Group>
  <Form.Group className="mb-3" controlId="formBasicCheckbox">
    <Form.Check type="checkbox" label="Has Conflict?" onChange={handleChange.bind(this)}/>
    {console.log(checked)}

  </Form.Group>
  {checked && 
  <Form.Group className="mb-3" controlId="formBasicEmail">
    <Form.Label>Describe Conflicts: </Form.Label>
    <Form.Control type="text" placeholder="Name" />
  </Form.Group> }
  <Button variant="primary" type="submit">
    Submit
  </Button>
</Form>
        </Card.Body>
      </Card>

    </>
  )
}
